using namespace std;

class Solution
{
public:
    bool isPossible(vector<int> &nums)
    {
        int n = nums.size();
        if (n < 3)
            return false;
        int last_n = 0, last_last_n = 0;
        deque<int> start = deque<int>{};
        for (int i = 0; i < n; ++i)
        {
            // cout << "开始判断 " << i << " " << nums[i] << endl;
            if (i != 0 && nums[i] < nums[i - 1])
                return false;

            if (i == 0 || nums[i] == nums[i - 1])
            {
                ++last_n;
            }
            if (i == n - 1 || nums[i] != nums[i + 1])
            {
                cout << "变化 " << last_n << " " << last_last_n << " " << nums[i] << endl;
                if (last_n > last_last_n)
                {
                    cout << "新增起点 " << nums[i] << " " << last_n - last_last_n << endl;
                    for (int j = 1; j <= last_n - last_last_n; ++j)
                        start.push_back(nums[i]);
                }
                else if (last_n < last_last_n)
                {
                    cout << "结束点判断 " << nums[i] << " " << last_last_n - last_n << endl;
                    for (int j = 1; j <= last_last_n - last_n; ++j)
                    {
                        if (nums[i - 1] + 1 - start.front() < 3)
                            return false;
                        start.pop_front();
                    }
                }
                last_last_n = last_n;
                last_n = 1;
            }
            if (i == n - 1 || nums[i] + 1 < nums[i + 1])
            {
                for (auto &a : start)
                {
                    cout << "中止 " << a << " " << nums[i] << endl;
                    if (nums[i] + 1 - a < 3)
                    {
                        return false;
                    }
                }
            }
        }
        return true;
    }
};

